import React, { Component } from 'react';
import { connect } from 'react-redux';
import Router from 'next/router'
import store from '../../redux/store';

class Loader extends Component {

  componentDidUpdate = () => {
    // if(typeof window !== 'undefined') {
    //   const myStorage = window.localStorage;
    //   myStorage.setItem('logged', true);
    // }
    const { Component: { name } } = this.props;
    const { logged } = store.getState();
    if (logged && name === 'Login') {
      Router.push('/profile')
    } else if (!logged && name === 'Profile') {
      Router.push('/login');

    }
  }

  render() {
    const { Component } = this.props;
    if (Component.name === 'main') {
      return (
        <Component />
      )
    }
    return (
      <main className="main-cloud">
        <Component />
      </main>
    )
  }
}

export default connect(store => store)(Loader);
