import React, { Component } from 'react';
import API from '../utils/api';

class News extends Component {
  state = {
    articles: [],
  }

  api = new API();

  componentDidMount = () => {
    this.api.getNews()
      .then(response => {
        return response.json();
      })
      .then(el => {
        this.setState({ articles: el.articles })
      });
  }

  render() {
    const { articles } = this.state;
    return (
      <div className="main-container">
        NEWS
      </div>
    )
  }
}

export default News;

const article = () => {
