import React, { Component } from 'react';
import PropTypes from 'prop-types';
import API from '../../../api/api';

class UserScrollList extends Component {
  state = {
    users: [],
  }

  api = new API();

  componentDidMount = () => {
    this.api.getUsers(12)
      .then((data) => {
        this.setState({ users: data.results });
      })
  }

  render() {
    // const { title, desc, theme, img } = this.props;
    const { users } = this.state;

    return (
      <div className="info-item info-item_tall">
        {users.map((item) => {
          return (
            <div className="user-scroll-item">
              <span className="user-scroll__name">{item.name.first} {item.name.last}</span>
              <b className="user-scroll__username">{item.login.username}</b>
            </div>
          )
        })}
      </div>
    )
  }
}

// InfoLineItem.propTypes = {
//   title: PropTypes.string,
//   desc: PropTypes.string,
//   theme: PropTypes.string,
//   img: PropTypes.string,
// };

export default UserScrollList;
