import React, { Component } from 'react';
import API from '../api/api';
import UserCard from '../components/user/user-card';
import SlideContainer from '../components/slide/slide-container';
import UserSlideMakeup from '../components/user/user-slide-makeup';
import OutsideClickHandler from 'react-outside-click-handler';

class Users extends Component<any, any> {
  state = {
    slideActive: false,
    activeUser: null,
    users: [] as Array<any>,
  }

  api = new API();

  componentDidMount = () => {
    this.api.getUsers(12)
      .then((data) => {
        let i = 0;
        const result = data.results.map((item) => {
          return ({
            ...item,
            uniqueID: i++,
          })
        })
        this.setState({ users: result });
      })
  }

  outsideClickHandler = () => {
    this.setState({ 
      activeUser: null,
      slideActive: false,
    });
  }

  clickOnCard = (uniqueID) => {
    this.setState({ 
      activeUser: uniqueID,
      slideActive: true,
    });
  }

  render() {
    const { users, activeUser } = this.state;
    console.log(users);

    return (
      <div className="users-page">
        <SlideContainer
          activeUser={Boolean(users[activeUser])}
          outsideClickHandler={this.outsideClickHandler}
        >
          <UserSlideMakeup
            activeUser={users[activeUser]}
          />
        </SlideContainer>
        <div className="users-page__title">Users</div>
        <div className="users-list">
          {users.map((item) => {
            return (
              <UserCard
                {...item}
                key={item.uniqueID}
                clickOnCard={this.clickOnCard}
              />
            )
          })}
        </div>
      </div>
    )
  }
}

export default Users;
