import React, { Component } from 'react';
import Head from 'next/head';
import Header from '../components/header/header';
import Sidebar from '../components/sidebar/sidebar';
import Loader from '../components/loader/loader';
import Login from './login';
import '../styles/index.scss';
import { Provider } from 'react-redux';
import store from '../redux/store';
import { autologin } from '../redux/actions';
import { capitalize } from '../utils/commonUtils';
import { connect } from 'react-redux';

class App extends Component {
  state = {
    logged: false,
  }

  componentDidMount = () => {
    if (typeof window !== 'undefined') {
      const myStorage = window.localStorage;
      const logged = myStorage.getItem('logged');
      this.setState({ logged: JSON.parse(logged) });
      if (JSON.parse(logged)) {
        store.dispatch(autologin());
      }
    }
  }

  render() {
    const { Component, router } = this.props;
    const { logged } = this.state;
    const path = router.pathname.slice(1, router.pathname.length) || 'Main';

    if (!logged) {
      return <Login />
    }

    return (
      <div className="app">
        <Head>
          <meta httpEquiv="expires" content="0" />
          <link rel="shortcut icon" href="/img/admin.ico" type="image/png" />
          <title> {capitalize(path)} - Dashboard </title>
        </Head>
        <Provider store={store}>
          <div className="main">
            <Sidebar />
            <div className="content-container">
              <Header />
              <Loader Component={Component} />
            </div>
          </div>
        </Provider>
      </div>
    )
  }
}


export default App;
